mapper.select:
SELECT i.item_id AS itemId, i.item_name AS itemName, s.quantity AS quantity, s.favorite_yn AS favoriteYn, im.path AS imageUrl FROM item i JOIN stock s ON s.item_id = i.item_id AND s.store_id = i.store_id LEFT JOIN image im ON im.image_id = i.image_id AND im.is_active = b'1'
i.store_id = #{storeId}
AND s.favorite_yn = 1
AND s.quantity = 0
AND i.item_name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY i.item_id DESC LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE item
SET item_name = #{itemName},
    image_id = #{imageId}
WHERE store_id = #{storeId}
    AND item_id = #{itemId}
    AND del_yn = b'0'
