mapper.select:
SELECT quantity
FROM stock
WHERE stock_id = #{stockId}

mapper.update:
UPDATE stock
SET quantity = #{quantity},
    favorite_yn = #{favoriteYn}
WHERE stock_id = #{stockId}

UPDATE stock
SET quantity = quantity + #{amount}
WHERE stock_id = #{stockId}

UPDATE stock
SET quantity = quantity - #{amount}
WHERE stock_id = #{stockId}
AND quantity >= #{amount}

mapper.select:
SELECT stock_id
FROM stock
WHERE store_id = #{storeId}
AND item_id = #{itemId}

SELECT count(*)
FROM stock
WHERE stock_id = #{stockId}
AND store_id = #{storeId}
