mapper.insert:
INSERT INTO item (
    store_id,
    item_name,
    image_id
)
VALUES (
           #{storeId},
           #{itemName},
           #{imageId}
       )

mapper.update:
UPDATE item
SET del_yn = b'1',
    updated_at = NOW()
WHERE store_id = #{storeId}
AND item_id = #{itemId}
AND del_yn = b'0'

UPDATE item
SET item_name = #{itemName},
    image_id = #{imageId}
WHERE store_id = #{storeId}
    AND item_id = #{itemId}
    AND del_yn = b'0'
