mapper.select:
SELECT r.request_id AS requestId, r.request_type AS requestType, i.item_name AS itemName, r.request_amount AS requestAmount, r.request_date AS requestDate, a.alba_name AS albaName, r.request_status AS requestStatus, r.created_at AS createdAt FROM request r JOIN item i ON r.item_id = i.item_id JOIN alba a ON r.alba_id = a.alba_id
i.store_id = #{storeId} AND r.is_active = b'1'
AND r.request_type = #{requestType}
AND r.request_status = #{requestStatus}
ORDER BY r.created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM request r
JOIN item i
    ON r.item_id = i.item_id


    i.store_id = #{storeId}
  AND r.is_active = b'1'


    AND r.request_type = #{requestType}


    AND r.request_status = #{requestStatus}
