mapper.update:
UPDATE stock s
JOIN item i
ON i.item_id = s.item_id
SET quantity = quantity + #{amount}
WHERE stock_id = #{stockId}
AND i.del_yn = b'0'

UPDATE stock s
JOIN item i
ON i.item_id = s.item_id
SET quantity = quantity - #{amount}
WHERE stock_id = #{stockId}
AND quantity >= #{amount}
AND i.del_yn = b'0'
