mapper.select:
SELECT alba.alba_id AS albaId, work.work_id AS workId, work.work_status AS workStatus, work.work_date AS workDate, alba.alba_name AS albaName, alba.alba_phone AS albaPhone, alba.alba_status AS albaStatus FROM alba as alba LEFT JOIN work as work ON alba.alba_id = work.alba_id
alba.store_id = #{storeId}
ORDER BY alba.alba_id DESC

SELECT COUNT(*) > 0
FROM alba
WHERE alba_name = #{albaName}

SELECT COUNT(*)
FROM alba
WHERE alba_id = #{albaId}

SELECT COUNT(*)
FROM alba
WHERE alba_phone = #{albaPhone}

SELECT EXISTS (
SELECT 1
FROM store
WHERE store_id = #{storeId}
)

mapper.update:
UPDATE alba
store_id = #{storeId}, alba_name = #{albaName}, alba_phone = #{albaPhone}, alba_status = #{albaStatus}, updated_at = NOW()
WHERE alba_id = #{albaId}
